export const StackBasic = () => {
  return (
    <Stack>
      <Box h="20" />
      <Box h="20" />
      <Box h="20" />
    </Stack>
  )
}

export const StackHorizontal = () => {
  return (
    <Stack direction="row" h="20">
      <Box />
      <Box />
      <Box />
    </Stack>
  )
}

export const StackWithHstack = () => {
  return (
    <HStack>
      <Box h="10" />
      <Box h="5" />
      <Box h="20" />
    </HStack>
  )
}

export const StackWithResponsiveDirection = () => {
  return (
    <Stack direction={{ base: "column", md: "row" }} gap="10">
      <Box boxSize="20" />
      <Box boxSize="20" />
      <Box boxSize="20" />
    </Stack>
  )
}

export const StackWithResponsiveSeparator = () => (
  <Stack
    mt={10}
    direction={["column", "row"]}
    separator={
      <StackSeparator borderColor={{ base: "green.500", md: "red.200" }} />
    }
    gap={4}
  >
    <Box flex="1" w={["100%", "40px"]} h="40px">
      1
    </Box>
    <Box flex="1" w={["100%", "40px"]} h="40px">
      2
    </Box>
    <Box flex="1" w={["100%", "40px"]} h="40px">
      3
    </Box>
  </Stack>
)

export const StackWithSeparator = () => {
  return (
    <Stack separator={<StackSeparator />}>
      <Box h="20" />
      <Box h="20" />
      <Box h="20" />
    </Stack>
  )
}

export const StackWithVstack = () => {
  return (
    <VStack>
      <Box w="50%" h="20" />
      <Box w="25%" h="20" />
      <Box w="100%" h="20" />
    </VStack>
  )
}
