"use client"

interface DialogProps {
  title: string
  description?: string
  content?: React.ReactNode
}

const dialog = createOverlay<DialogProps>((props) => {
  const { title, description, content, ...rest } = props
  return (
    <Dialog.Root {...rest}>
      <Portal>
        <Dialog.Backdrop />
        <Dialog.Positioner>
          <Dialog.Content>
            {title && (
              <Dialog.Header>
                <Dialog.Title>{title}</Dialog.Title>
              </Dialog.Header>
            )}
            <Dialog.Body spaceY="4">
              {description && (
                <Dialog.Description>{description}</Dialog.Description>
              )}
              {content}
            </Dialog.Body>
          </Dialog.Content>
        </Dialog.Positioner>
      </Portal>
    </Dialog.Root>
  )
})

export const OverlayBasic = () => {
  return (
    <>
      <Button
        onClick={() => {
          dialog.open("a", {
            title: "Dialog Title",
            description: "Dialog Description",
          })
        }}
      >
        Open Modal
      </Button>
      <dialog.Viewport />
    </>
  )
}

"use client"

interface AlertProps {
  title?: string
  content?: React.ReactNode
  onConfirm?: () => void
}

const alert = createOverlay<AlertProps>((props) => {
  const { onConfirm, content, title = "Are you sure?", ...rest } = props
  const cancelRef = useRef<HTMLButtonElement | null>(null)
  return (
    <Dialog.Root
      {...rest}
      role="alertdialog"
      initialFocusEl={() => cancelRef.current}
    >
      <Portal>
        <Dialog.Backdrop />
        <Dialog.Positioner>
          <Dialog.Content>
            <Dialog.Header>
              <Dialog.Title>{title}</Dialog.Title>
            </Dialog.Header>
            <Dialog.Body spaceY="4">{content}</Dialog.Body>
            <Dialog.Footer>
              <Dialog.ActionTrigger asChild ref={cancelRef}>
                <Button variant="outline">Cancel</Button>
              </Dialog.ActionTrigger>
              <Button colorPalette="red" onClick={onConfirm}>
                Delete
              </Button>
            </Dialog.Footer>
          </Dialog.Content>
        </Dialog.Positioner>
      </Portal>
    </Dialog.Root>
  )
})

export const OverlayWithAlert = () => {
  return (
    <>
      <Button
        onClick={async () => {
          alert.open("a", {
            onConfirm() {
              console.log("confirmed")
              alert.close("a")
            },
            content:
              "Remove this item will clear all the data and cannot be undone.",
          })
        }}
      >
        Open Modal
      </Button>
      <alert.Viewport />
    </>
  )
}

